uniform float u_time;

float rect(in vec2 st, in vec2 size){
	size = 0.25-size*0.25;
    vec2 uv = step(size,st*(1.0-st));
	return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


    vec2 st = fragCoord/iResolution.xy;

    vec3 influenced_color = vec3(0.563,0.580, 0.556);
    vec3 influencing_color_A = vec3(0.040,0.040,0.040);
    vec3 influencing_color_B = vec3(0.937,0.981,1.000);

    vec3 color = vec3(0.);

    // Background Gradient
    color = mix( influencing_color_A,
                 influencing_color_B,
                 st.y*abs(sin(iTime)));

    // Foreground rectangle
    vec2 size = vec2(0.020,0.460);
    vec2 offset = vec2(.3,0.);
    color = mix(color,
               influenced_color,
               rect(st,size));

    color = mix(color,
               influenced_color,
               rect(st+offset,size));

    color = mix(color,
               influenced_color,
               rect(st-offset,size));

    fragColor = vec4(color,1.0);
}
