#define PI 3.1415926535

#define time iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //get uv coordinates
   vec2 uv = (fragCoord.xy - iResolution.xy)/iResolution.y;

   //set offset and scale
   uv += vec2(1.4, 1);
   uv *= 0.5;

   //store color
   vec3 color = vec3(0);

   //create overlap pattern

   //get circle shape
   float circle = sin((uv.x + uv.y)* PI ) + cos( (uv.y - uv.x) * PI);

   //create pulse pattern
   circle = 1.- mod(circle + time , .3 * pow(time * 0.8, .06));

   //apply color to pattern
   color = (vec3(0., .5, .7) * sin(time + circle) + vec3(.5, 0,1) * cos(circle + time * PI) + vec3(1, 0,0))  * circle;


   fragColor = vec4(color, 0);

}
