#define pi 3.14159265359

const float speed = 7.;
const float zoom = 0.3;

float get_light(vec2 uv) {
    vec2 stride = 7./iResolution.xy;
    for (float i = 0.; i < 10.; ++i) {
        vec4 col_1 = texture(iChannel0, uv + i *vec2(stride.x, stride.y));
        vec4 col_2 = texture(iChannel0, uv + i *vec2(-stride.x, -stride.y));
        vec4 col_3 = texture(iChannel0, uv + i *vec2(-stride.x, stride.y));
        vec4 col_4 = texture(iChannel0, uv + i *vec2(stride.x, -stride.y));
        if (max(col_1.a, max(col_2.a, max(col_3.a, col_4.a))) > 0.5) {
            return 1. - i / 10.;
        }
    }
    return 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = (sin(speed*iTime)*0.5 + 0.5) * step(0., sin(speed*iTime - pi/2.));

    vec2 uv = fragCoord/iResolution.xy;
	vec4 forground = texture(iChannel0, uv);   
    vec4 background = texture(iChannel1, uv);

    uv = (uv - 0.5) * (1. - zoom * ratio) + 0.5;
    vec4 zoomed_forground = texture(iChannel0, uv);

    forground = mix(forground, zoomed_forground, 
                    zoomed_forground.a);
    forground.rgb *= 1. + ratio;

    float light = get_light(uv);
    background += light * ratio;

    fragColor = mix(background, forground, forground.a);
}

float th = 20.0; // threshold
float a2 = 1.2;

float getAlpha(vec4 c){
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fg = texture(iChannel0,uv);

    float alpha = step(0.5, getAlpha(fg));

	fragColor = vec4(fg.rgb, alpha);
}
