void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float _irregular = iMouse.x/iResolution.x;
    float _zoom = iMouse.y/iResolution.y;

    vec2 u = _zoom * 15.*fragCoord/iResolution.x;

    vec2 s = vec2(1.,1.732);
    vec2 a = mod(u     ,s)*2.-s;
    vec2 b = mod(u+s*.5,s)*2.-s;


    vec2 myuv = vec2(.5*min(dot(a*_irregular,a),dot(b,b)));
    myuv *=.5;
    myuv +=.5;



   fragColor = texture(iChannel0,myuv );


}
