mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(vec2(c, -s),vec2( s, c));
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float butterfly(vec2 uv) {
    uv-= 0.5;
    uv*= 1.5;
    uv.x*=1.5;
    uv.x *= 1.0 -sin(2.0*2.)*.2;
    vec2 p =uv*20.0;

   	float r = length(p);
	float t = atan(p.y, p.x);

    float butterfly = 
        7. - 0.5*sin( 1.*t) 
           + 2.5*sin( 3.*t) 
           + 2.0*sin( 5.*t) 
           - 1.7*sin( 7.*t) 
           + 3.0*cos( 2.*t) 
           - 2.0*cos( 4.*t) 
           - 0.4*cos(16.*t) - r;

    float a =-2.0;
    float b = 0.0;
    float c = smoothstep(a, b, butterfly);
    float g = normalize(p.x);
    return g*c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	vec3 col=vec3(1.0,0.5,0.1);

    float c=sdBox(p,vec2(0.5));

    if(c<=0.0){

    p.x-=0.5;
    p.y+=0.5;
    p.y/=0.8;
    vec2 p1=p;


    p1.x=abs(p1.x);
    float b2=butterfly(p1);
    p1=p;

    p1.x+=1.0;

    float b=butterfly(p1);



    b=min(b,b2);









     if(b < 0.0) col = vec3(1.0,0.0,0.0);

 }



	fragColor = vec4(col,1.0);
}
