void mainImage( out vec4 fragColor, in vec2 f )
{
    vec2 res = iResolution.xy;
    vec2 uv = f.xy / max(res.x,res.y);
    float t = iTime * 0.37;    
    vec2 u = fract(uv * 10.);
    u.x += uv.x + fract(t) * 4.0 - 2.0;
    float m = abs(u.x + u.y * - 1.);
    m = smoothstep(m, m + 20. / res.x, 0.3);
    fragColor = vec4(m);
}

