// Ambient occlussous marinion
// p : Point
// n : Normal
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n, int a)
{
    if (a==0) { return 1.0; }

	float ao=0.0; 

    for (int i=0;i<a;i++)
    {
 		vec3 d = Cosine(581.123*float(i) , n);

        int s;
        bool h;
        float t=SphereTrace(p,d,10.0,h,s);
        if (!h) {ao+=1.0;}
        else if (t>5.0)
            {
             ao+=1.0;   
            }
    }

    ao/=float(a);
	return ao;
}

