vec3 Style(in vec3 p,in vec3 n) {
    float u=sin(iTime)*10.0*cos(iTime)*10.0;
    float n1, n2, n3;
    n1 = sqrt( sin(p.x+Noise(p)*u + p.y*1.5 + p.z*2.0) );
    n2 = n1 / 2.0;
    n3 = sqrt( sin(p.x + p.y + p.z)*n2 );
    return mix(vec3(0.0 +Noise(p)*iTime, 10.0+Noise(p)*iTime, 150.0)/255.,
               vec3(255.0, 255.0, 255.0)/255.,
               n3 );
}

