void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

float note(float time, float hz) {
 	return sin(6.2831 * hz * time);  
}

vec2 mainSound( in int samp, float time )
{
    // A 440 Hz wave that attenuates quickly overt time
    float beati = floor(time * 4.);
    float beatf = fract(time * 4.);

    // pitch
   	float s = note(time, 220. * (1.0 + random(vec2(beati, 1.0))));
    // envelope
    s *= sin(beatf * 3.14) * step(.5, random(vec2(beati, 2.0)));

    // drum
    s += smoothstep(1., .0, beatf) * random(vec2(time));

    return vec2(s );
}
