// Exercise following the book of shader - Shapes
// Inspiration: Author @patriciogv - 2015 - http://patriciogonzalezvivo.com

// animating flower shapes 


float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    vec2 pos = vec2(0.5)-st;
    float pct = plot(st,0.25);

    float r = length(pos)*5.0-pct;
    float a = atan(pos.y,pos.x); 
    float f = cos(a*abs(sin(iTime))*10.);
    // f = abs(cos(a*3.));
    // f = abs(cos(a*2.5))*.5+.3;
    f = abs(cos(a*10.*abs(cos(iTime)))*sin(a*abs(cos(iTime))*5.0))*0.8+.1;
    //f = smoothstep(-.5,1., cos(a*20.))*0.2+0.5;

    color = vec3( 1.-smoothstep(f,f+0.2*cos(iTime),r) );

    fragColor = vec4(color, 1.0);
}
