void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u - .5*R ) / 50., V; 
    if ( u.y/R.y < .25 ) U /= 4.;                            // bottom : zoom
    V = abs(fract(U)-.5);                                    // tiles + symmetry
    float m = min(V.x,V.y);
    O = m < .02         ? vec4(.7)                           // grey grid
      : m<.04 && U.x<0. ? vec4(1)                            // surrounding white (left half of window )
      :                   vec4(.6,1,1,0);                    // cyan background
}
