void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	float td = iTime*.5;
    vec3 col = vec3(0);

	vec3 ro = vec3(1,0 , -1);
    vec3 lookat = vec3(-6,0,0);
    float zoom = mix(.68, 1., abs(sin(td))*2.);

    vec3 f = normalize(lookat-ro),
    	r= normalize(cross(vec3(0,1,0),f)),
        u = cross (f,r),
        c =  ro + f * zoom,
        i = c+uv.x * r + uv.y * u,
        rd = normalize(i-ro);
    float dS, dO;
    vec3 p;

    for (int i=0; i<100; i++){
    	p=ro + rd* dO;
        dS =-( length(vec2(length(p.xz)-1.,p.y))-mix(.5,.7, abs(sin(td))*2.));
        if (dS<0.001) break;
        dO+=dS;
    }
    if (dS<0.001) {
        float x = atan(p.x, p.z)+td*1.2;
        float y = atan(length(p.xz)-1.,p.y);
        float bands =sin(x*5.+y*100.);
        float waves = sin(y*10.+ x*70.);
        float b1 = smoothstep(.3,.04,bands);
        float b2 = smoothstep(.02,.05,bands-.5);
        float m = b2 * (1.-b1);
        m = max(m, waves);
        col+=1.-m;
    }
    col += 0.5 + 0.5*sin(td+uv.xyx+vec3(2,2,2));
    fragColor = vec4(col,1.0);
}
