#define PI 3.141592

float scene(vec2 p, float t) {

    vec2 p_pol = vec2(length(p), atan(p.y, p.x));
    p_pol.y *= 7.0;

    p_pol.y += iTime;
    p_pol.x = 1.0 - abs(fract(p_pol.x - t / -4.0) * 6.0 - 1.0);
    //p_pol.x -= 1000.0;

    //p = p_pol.x * vec2(cos(p_pol.y), sin(p_pol.y));

    p = p_pol.x * vec2(cos(p_pol.y), sin(p_pol.y));
 	float val = 1000.0;
    val = min(val, length(p - vec2(0.0, 1.0)) - 0.9);
    val = min(val, length(p - vec2(cos(t * PI), sin(t * PI))) - 0.31312);


    return val;
}





void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    float t =  60.0 * iChannelTime[3] / 60.0;
	vec2 p = (uv * 2.0 - 1.0) * iResolution.xy / iResolution.yy;


    vec3 val = vec3(
        scene(p, t - 1.0 * abs(fract(t / 16.0) * 2.0 - 1.0)),
        scene(p, t),
        scene(p, t + 1.0 * abs(fract(t / 2.0) * 2.0 - 1.0))
    );

    vec3 color_bb = texture(iChannel0, uv.xy - 4.0 * normalize(p) / iResolution.xy).rgb;
    vec3 color_vd = texture(iChannel1, uv.xy).rgb;
    vec3 color_mask = textureLod(iChannel2, uv.xy, 1.0).rgb;

    vec3 color = vec3(1.0, 1.0, 1.0);



    color *= smoothstep(0.0, 0.2, abs(fract(val / 16.0 - t / 2.0) * 2.0 - 1.0));

    color *= abs(fract(val / 2.0) * 2.0 - 1.0);


    color = mix(color, color_bb * mix(color_vd + 0.5, vec3(0.5, 1.0, 0.0), 0.5), 1.0 - smoothstep(-0.10, 0.3, color_vd.g- color_vd.r));
    color = mix(color, color_bb, smoothstep(0.9, 1.0, length(color)));

    color = mix(color, vec3(cos(t * PI), sin(t * PI), cos(t * 2.0 * PI)), step(0.01, color_mask.r));


    // Output to screen
    fragColor = vec4(color,1.0);
}

