void mainImage( out vec4 o, in vec2 u)
{
    vec2 uv = (u*2.-R)/R.y;
    if (max(abs(uv.x),abs(uv.y)) > 1.) {
        o = vec4(0);
        return;
    }
    uv = (uv*.5+.5)*(SIZE/R);
    o = textureLod(iChannel1,uv,0.);
    //o = vec4(abs(textureLod(iChannel0,uv,0.).x-textureLod(iChannel1,uv,0.).x)*10.);
}

void mainImage( out vec4 o, in vec2 u)
{
    if (max(u.x,u.y)-SIZE >= 0.) return;
    o = vec4(texelFetch(iChannel0,ivec2(u),0).xyz,0);
}

#define qidentity vec4(0,0,0,1)
vec4 qfromEuler(vec3 eu) {//yaw(x)-pitch(y)-roll(z)
    vec3 sn = sin(eu.yxz*.5),
        cs = cos(eu.yxz*.5);
    return vec4(cs.z*cs.y*sn.x-sn.z*sn.y*cs.x,
                cs.z*sn.y*cs.x+sn.z*cs.y*sn.x,
                sn.z*cs.y*cs.x-cs.z*sn.y*sn.x,
                cs.z*cs.y*cs.x+sn.z*sn.y*sn.x);
}
vec4 qconjugate(vec4 q) {
 	return vec4(-q.xyz,q.w);   
}
vec4 qmult(vec4 a, vec4 b) {
    return vec4(a.y*b.z-a.z*b.y+a.x*b.w+a.w*b.x,
                a.z*b.x-a.x*b.z+a.y*b.w+a.w*b.y,
                a.x*b.y-a.y*b.x+a.z*b.w+a.w*b.z,
                a.w*b.w-a.x*b.x-a.y*b.y-a.z*b.z);
}

void mainImage( out vec4 o, in vec2 u)
{    
    if (iFrame == 0) {
        o = texelFetch(iChannel1,ivec2(u),0);
        return;
    }
    if (iFrame > 2) {
        o = texelFetch(iChannel0,ivec2(u),0);
        return;
    }
    if (max(u.x,u.y)-SIZE >= 0.) return;

    float t = 0., st = floor(u.x)*PI/SIZE;
    vec4 v = vec4(0);
    for (int x = 0; x < int(SIZE); x++) {
        vec4 s = texelFetch(iChannel0,ivec2(x,u.y),0);
     	v += qmult(s,qfromEuler(vec3(t,0,0)))+qmult(s,qfromEuler(vec3(0,t,0)))+qmult(s,qfromEuler(vec3(0,0,t)));
        t += st;
    }
    if (iFrame == 1) v /= SIZE*12.;
    o = v;
}
