#define R iResolution.xy
mat2 r (float a) {
    float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}
vec4 ro (vec2 U) {
    vec3 v = vec3(2.*U,1);
    v.yz *= r (iTime);
    v.zx *= r (iTime);
    return vec4(v,(length(v.xy)-abs(v.z)));
}
void mainImage( out vec4 Q,vec2 U )
{
 	U = 2.*(U-0.5*R)/R.y;

    vec4 
        v = ro(U),
    	n = ro(U+vec2(0,1)/R),
    	e = ro(U+vec2(1,0)/R),
    	s = ro(U-vec2(0,1)/R),
    	w = ro(U-vec2(1,0)/R);
    vec3 g = normalize(vec3(e.w-w.w,n.w-s.w,.01));
    float h = dot(g,ro(vec2(1,0)).xyz);
    Q = .2*vec4(0.5+0.5*h*h*h)*smoothstep(0.,-0.1,v.w);
    Q.xz += smoothstep(.1,.05,abs(v.w));
    Q += smoothstep(.05,0.025,abs(v.x));
    Q += smoothstep(.05,0.025,abs(v.y));
    Q += smoothstep(.05,0.025,abs(v.z));
    Q.xy += smoothstep(.25,.2,length(v.xy-v.z*vec2(cos(3.*iTime),sin(3.*iTime))));
    Q.yz += smoothstep(.25,.2,length(v.xy-v.z*vec2(-cos(3.*iTime),-sin(3.*iTime))));
	Q *= exp(-length(U));



}
