const vec2 juliaC = vec2(0.3, -0.5);
const float juliaScale = 0.01;
const float juliaZoom = 0.5;
const int maxIterations = 255;

vec2 complexZ(const in vec2 z)
{
	return vec2
    (
 		z.x * z.x - z.y * z.y,
 		z.x * z.y * 2.0
	);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy - iResolution.xy * 0.5;
	uv *= 2.0 / min(iResolution.x, iResolution.y);

    vec2 z = uv * juliaZoom;
    int count = 0;
    for (; count < maxIterations; ++count) 
    {
 		z = complexZ(z) + juliaC;
 		if (dot(z, z) > 2.0)
        {
            break;
 		}
    }

    vec3 fractalCol = texture(iChannel0, uv * 1.5).rgb;
    fractalCol -= vec3(1.0);  
    fractalCol = mix(fractalCol, vec3(0.12, 0.1, 0.12), 0.825);   

	fragColor = vec4(fractalCol * float(count) * juliaScale, 1.0);
    fragColor.rgb = vec3(pow(fragColor.rgb, vec3(0.4545)));
}
