void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Self note: fragCoord = gl_FragCoord.xy
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 centeredUV = uv - vec2(0.5, 0.5);

    // Time varying pixel color
    vec3 col = 0.6 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float r = 0.2 + 0.1 * cos(atan(centeredUV.y, centeredUV.x) * 3.0 + 5.0 * sin(iTime * 5.0) * centeredUV.x);
    r += 0.1 * cos(120.0 * centeredUV.y);
    col *= smoothstep(r + (sin(iTime) + 1.0) / 3.0, r - .1, length(centeredUV));

    // Output to screen
    fragColor = vec4(col,1.0);
}
