void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalization
    vec2 uv = fragCoord/iResolution.xy;


   	// Loading texture
    vec4 op = texture(iChannel0, uv);


    // Changin colors based on time
    fragColor = op;
    fragColor.x = sin(op.x*(iTime*uv.x)); 
    fragColor.y = tan(op.x*(iTime*uv.x)-50.); 


}
