void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Coordinate system
    vec2 uv = 1.*(2.*fragCoord-iResolution.xy)/iResolution.y;

    float x = uv.x;
    float y = uv.y;

    //Implicit function (equal to y=-x)
    float f = (x+sqrt(1.+x*x))*(y+sqrt(1.+y*y))-1.;

    //Plot
    fragColor = vec4(abs(f)/fwidth(f));
}
