// #define yaxis

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float mDist = 9999.9;
    vec2 mUv = fragCoord.xy / iResolution.xy;

    for (int x = 0; x < int(fragCoord.x); x++) {
        #ifdef yaxis
        for (int y = 0; y < int(fragCoord.y); y++) {
        #else
        int y = int(fragCoord.y);
        #endif
            vec2 uv = vec2(x, y) / iResolution.xy;
			vec2 trueUv = fragCoord.xy / iResolution.xy;

            vec3 colTex = texture(iChannel0, uv).rgb;
            vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

            vec3 distVec = abs(colTex - col);
            float dist = (distVec.r + distVec.g + distVec.b) / 3.0;

            if (dist < mDist) {
                mUv = uv;
                mDist = dist;
            }
		#ifdef yaxis
        }
        #endif
    }

    vec3 col = texture(iChannel0, mUv).rgb;
    fragColor = vec4(col, 1.0);
}
