vec3 red = vec3(1.,0.,0.);
vec3 blue = vec3(0.,0.,1.);
vec3 white = vec3(1.,1.,1.);
vec3 yellow = vec3(1.,1.,0.);


float quad (vec2 uv, vec2 pos, vec2 size)
{
    float value = step(pos.x,uv.x) - step(pos.x+size.x,uv.x);
    value *= step(pos.y,uv.y)-step(pos.y+size.y,uv.y);

    return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 paint = vec3(0.0);

    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    paint += quad(uv, vec2(0.*ratio,0.), vec2(.7,0.1)) * white;
    paint += quad(uv, vec2(0.*ratio,0.12), vec2(.7,.1)) * white;
    paint += quad(uv, vec2(0.*ratio,0.24), vec2(.7,.4)) * blue;
    paint += quad(uv, vec2(0.*ratio,0.66), vec2(.7,.2)) * white;
    paint += quad(uv, vec2(0.*ratio,0.875), vec2(.7,.15)) * red;

    paint += quad(uv, vec2(0.4*ratio,0.),vec2(.2,.1)) * white;
    paint += quad(uv, vec2(0.4*ratio,0.12),vec2(.2,.1)) * white;
    paint += quad(uv, vec2(0.4*ratio,0.24),vec2(.2,.4)) * white;
    paint += quad(uv, vec2(0.4*ratio,0.66),vec2(.2,.2)) * yellow;
    paint += quad(uv, vec2(0.4*ratio,0.875),vec2(.2,.2)) * white;

    paint += quad(uv, vec2(0.52*ratio,0.), vec2(.3,.1)) * white;
    paint += quad(uv, vec2(0.52*ratio,0.12), vec2(.3,.1)) * red;
    paint += quad(uv, vec2(0.52*ratio,0.24),vec2(.3,.4)) * white;
    paint += quad(uv, vec2(0.52*ratio,0.66),vec2(.3,.2)) * white;
    paint += quad(uv, vec2(0.52*ratio,0.875),vec2(.3,.2)) * white;

    paint += quad(uv, vec2(0.695*ratio,0.), vec2(.6,.1)) * yellow;
    paint += quad(uv, vec2(0.695*ratio,0.12), vec2(.6,.1)) * white;
    paint += quad(uv, vec2(0.695*ratio,0.24),vec2(.6,.4)) * white;
     paint += quad(uv, vec2(0.695*ratio,0.66),vec2(.6,.2)) * blue;
     paint += quad(uv, vec2(0.695*ratio,0.875),vec2(.6,.2)) * white;
    // Output to screen
    fragColor = vec4(paint,1.0);
}
