const float maxi = 10.;

float Tiles(vec2 uv, vec2 p, float radius, float blur, float offset){

    float col =0.;
    float d = length(uv -p);
    for (float i=0.; i<30.; i+=2.){
		col +=  smoothstep(blur, 0., abs( d - (radius - i * offset) ) );

        /*col +=  smoothstep(radius - i * offset, radius - i * offset - blur, d)
            - smoothstep(radius - (i + 1.) * offset, radius - (i + 1.) * offset - blur, d);*/
   }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    float radius = 0.07;
    float offset = radius * .05;
    float blur = 1.5 / iResolution.y;
    float col;

    float time = sin(iTime) *.5 +.5;

    for (float i=0.; i<maxi; i++){

        vec2 p = vec2(sin(i + iTime * .1), cos(i)) * .2;

    	col += Tiles(uv, p, radius, blur, offset);
    	radius += radius / maxi;
    	col *= 1. -(1. / maxi);


    }


    fragColor = vec4(vec3(pow(col, 1./2.2)),1.0);
}
