#define pi 3.14159265359

float speed = 7.;
float distortion_speed = 40.;
float distortion = 0.1;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = (sin(speed*iTime)*0.5 + 0.5) * step(0., sin(speed*iTime - pi/2.));

    vec2 uv = fragCoord/iResolution.xy;
    vec4 forground = texture(iChannel0, uv);

    uv += ratio * vec2(
        distortion * sin(7. * uv.x + distortion_speed * iTime), 
        distortion * cos(10. * uv.y + distortion_speed * iTime)
    );
    vec4 background = texture(iChannel1, uv);

    float noise = texture(iChannel2, mod(uv*2., 1.)).r * 0.7 + 0.3;
    float alpha = step(0.0001, ratio);
    background = mix(
        background, vec4(noise,noise,1,1),
        0.8 * alpha * texture(iChannel0, uv - vec2(0., 0.1)).a
    );
    background = mix(
        background, vec4(1,noise,noise,1),
        0.7 * alpha * texture(iChannel0, uv + vec2(0.1, 0.)).a
    );
    background = mix(
        background, vec4(1,1,1,1),
        0.8 * alpha * texture(iChannel0, uv + vec2(0.02, -0.05)).a
    );


    fragColor = mix(background, forground, forground.a);
}

float th = 20.0; // threshold
float a2 = 1.2;

float getAlpha(vec4 c){
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fg = texture(iChannel0,uv);

    float alpha = step(0.5, getAlpha(fg));

	fragColor = vec4(fg.rgb, alpha);
}
