void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 st = 2.0 * uv - 1.0;
    vec3 color = vec3(1.0);

    float t = iTime;

    float r = length(st);
    float a = atan(st.y, st.x);

    float v = cos(50.0 * (sqrt(r) - 0.00 * a - 0.1 * t));

    float bending = 2.0;
    float n = 8.0;

    v = cos((bending * sqrt(r) + 1.0 * a - 0.5 * t) * n);
    //v = cos((bending * pow(r, 1.5) + 1.0 * a - 0.5 * t) * n);
    //v = cos((bending * tan(r) + 1.0 * a - 0.5 * t) * n);
    //v = cos((bending * cosh(r) + 1.0 * a - 0.5 * t) * n);
    //v = cos((bending * sinh(r) + 1.0 * a - 0.5 * t) * n);

    color = vec3(v);
    fragColor = vec4(color, 1.0);
}
