vec3 hsv2rgb( in vec3 c )
{
    return c.z*mix(vec3(1),max(abs(mod(c.x/60.+vec3(0,4,2),6.)-3.)-1.,0.),c.y);
}

const float PI = 3.1315;

float rad2deg(float a) {
    return a/PI*180.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord)/iResolution.xy;

    float anim = mod(iTime*0.1f,1.);

    vec3 col = hsv2rgb(vec3(rad2deg((uv.x+anim)*2.*PI),uv.y,1.f));

    fragColor = vec4(col,1.0);
}
