// by Samvel Asatryan, 21/02/2020
// based on https://www.crwflags.com/fotw/images/n/no-dimen.gif
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // declare colors to use
    vec3 RED = vec3(1., 0., 0.);
    vec3 WHITE = vec3(1., 1., 1.);
    vec3 BLUE = vec3(0., 0., 1.);

    vec3 color = WHITE;

    // normalize dementions 0 < x < 24, 0 < y < 16
	float x = fragCoord.x / iResolution.x * 24.;
    float y = fragCoord.y / iResolution.y * 16.;

    // first column
    if (x < 7.) {
        if (y < 7.) {
        	color = RED;            
        } else if (y < 8.) {
            color = WHITE;
        } else if (y < 10.) {
            color = BLUE;
        } else if (y < 11.) {
            color = WHITE;
        } else {
            color = RED;
        }
    } else if (x < 8.) {
        // second (white) column
        if (y > 8.) {
            if (y < 10.) {
                color = BLUE;
            }
        }
    } else if (x < 10.) {
        // third (blue) column
        color = BLUE;
    } else if (x < 11.) {
        // fourth (white) column
        if (y > 8.) {
            if (y < 10.) {
                color = BLUE;
            }
        }
    } else {
        // last column (the same with first column)
    	if (y < 7.) {
        	color = RED;            
        } else if (y < 8.) {
            color = WHITE;
        } else if (y < 10.) {
            color = BLUE;
        } else if (y < 11.) {
            color = WHITE;
        } else {
            color = RED;
        }
    }

    fragColor = vec4(color, 1.0);
}
