void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 st = fragCoord.xy/iResolution.xy;
    float pct = 0.0;
    st = vec2(0.5) - st;
    st.x *= iResolution.x/iResolution.y;
    st *= 5.0;


    pct = distance(st,vec2(0.0));
    for(float i=0.0;i < 10.0; i+=1.0){
        float cr = cos(3.14*2.0*iMouse.x/iResolution.x)*0.5;
        float sr = sin(3.14*2.0*iMouse.y/iResolution.y)*0.5;
		pct *= distance(st,vec2(cos(0.2*3.14*cr*i+iTime),sin(0.2*3.14*sr*i+iTime))*(sin(iTime)*0.5+1.0));  
    }
    pct *= distance(st,floor(st)+vec2(0.5));

    //vec3 color = vec3(smoothstep(abs(sin(u_time)*0.6),0.9,1.0-pct))*vec3(0.196,0.020,0.430);
	vec3 color = vec3(smoothstep(0.6,0.8,1.0-pct))*vec3(sin(st.x+iTime)*0.5+0.5,cos(st.y+iTime)*0.5+0.5,0.430);
	fragColor = vec4( 1.0 - color, 1.0 );
}
