// From this video https://youtu.be/2SzmkCseDi0?t=337

float tau = 6.28;

float doPatt(vec2 uv){

    vec2 uvp = vec2(atan(uv.y, uv.x)/tau, length(uv));

    float uvpb = mod(uvp.x*80.,1.) - 0.5;

    uvpb *= length(uvp.y)*4.;

    return smoothstep(0.2,0.01, abs(uvpb) - 0.2 + - sin(iMouse.x/iResolution.x)*0.2 )*(smoothstep(0.,1.,length(uv)*2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(1);



    col -= doPatt(uv);
    col -= doPatt(uv + vec2(0.04 + sin(iTime)*0.04, 0.04 + sin(iTime)*0.04));
    col -= doPatt(uv + vec2(-0.05, 0.1));


    fragColor = vec4(col,1.0);
}
