void mainImage(out vec4 color, in vec2 coord)
{
    coord.x += 5. * sin(iTime * 4. + coord.y / 4.);
    coord.x -= 10. * sin(iTime * 4.);
    vec2 s = iResolution.xy*.5*vec2(1.-(sin(iTime * 2.)/4.));
    vec2 c = coord - s; c.y *= -1.;

    vec2 a = step(abs(c-s.y*.35), s.yy*.4);
    vec2 b = step(abs(c+s.y*.15), s.yy*.6);
    vec3 col = (a.xxx*a.y-.5)*(b.x*b.y-.5)*4.;

    color = vec4(col,1);
}
