float stipple(float c) {
   return clamp(0.0, 1.0, sin(c * 3.14 * 190.0)+.7);
}

float twoband(vec2 p, float s, float width, float gap) {
    float hw = width / 2.0;
    float hg = gap / 2.0;
    float c = abs(p.x) - hg;
    return smoothstep( hw+s, hw, abs(c) );;
}

float threeband(vec2 p, float s, float width, float gap1, float gap2) {
    float hw = width / 2.0;
    float hg1 = gap1 / 2.0;
    float hg2 = gap2 / 2.0;
    float c = abs(p.x) - hg1;
    c = abs(c) - hg2;
    return smoothstep( hw+s, hw, abs(c) );
}

float green(vec2 p) {
    float pat = twoband(p.xy, 0.002, 0.12, 0.16) * mix(0.6, 1.0, stipple(p.y))
        + twoband(p.yx, 0.002, 0.03, 0.16) * mix(0.6, 1.0, stipple(p.x));
    return pat / 4.0;
}

float red(vec2 p) {
    float pat = twoband(p.xy, 0.002, 0.22, 0.3) * mix(0.6, 1.0, stipple(p.y))
        + twoband(p.yx, 0.002, 0.22, 0.3) * mix(0.6, 1.0, stipple(p.x));
    return pat / 4.0;
}

float white(vec2 p) {
    return threeband(p.xy, 0.002, 0.007, 0.3, 0.03) * mix(0.6, 1.0, stipple(p.x + p.y))
        + threeband(p.yx, 0.002, 0.007, 0.3, 0.03) * mix(0.6, 1.0, stipple(p.x - p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

    vec3 col = vec3(
        red(fract(uv * 5.0 + 1.5) - .5),
        green(fract(uv * 5.0) - .5),
        0
    );

    col += vec3(min(1.0, white(fract(uv * 5.0 + 1.5) - .56) + white(fract(uv * 5.0 + 1.5) - .44)) * 0.3);

    fragColor = vec4(col,1.0);
}
