float GetDist(vec3 p  ) 
{	

    float d, dif1, dif2;
    vec2 res;
    vec3 pp,p1, p2,p3; 
    res = vec2(9999, 0);


	vec3 q=p;
    float cx=3.0*6.0;
    float cy=3.0;
    float cz=12.0;

    q.x = mod(q.x,12.0)-3.0;

    q.z = mod(q.z,12.0)-6.0;
    q.y = mod(q.y,12.0)-6.0;

    p=q;

    p= rotate_x(p, radians(90.0));


    float sdb1= sdBox(p-vec3(0.0,0.0,3.0), vec3(3.0,1.0,3.0) );
    float sdb2= sdBox(p-vec3(6.0,0.0,3.0), vec3(3.0,1.0,3.0) );


    float sdb4= sdBox(p-vec3(0.0,0.0,-3.0), vec3(3.0,1.0,3.0) );
    float sdb5= sdBox(p-vec3(6.0,0.0,-3.0), vec3(3.0,1.0,3.0) );


    res =opU(res, vec2(sdb2,2.0)); //rojo
    res =opU(res, vec2(sdb4,4.0)); //azul


    res =opU(res, vec2(1.0,0.0)); 


    d = res.x;
    mObj.dist = res.x;
    mObj.idcolor = res.y;

    return d;
}



//---------actualizacion por Shane---28-may-2020    ...gracias
float RayMarch(vec3 ro, vec3 rd) 
{

    // The extra distance might force a near-plane hit, so
    // it's set back to zero.
    float dO = 0.; 
    //Determines size of shadow
    for(int i=0; i<MAX_STEPS; i++) 
    {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);

        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
        dO += dS;

    }

    return dO;
}




//---------------------------------------------------

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}


float GetLightPar(vec3 p, vec3 plig) {
    vec3 lightPos = plig;
    //Determine movement of light ex. shadow and light direction and diffusion

    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


