#define blurEdge 0.02

float circle(in vec2 _st,in vec2 pos, in float _radius){
    vec2 dist = _st-pos;
	return smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         length(dist));
}

float ring(in vec2 uv,in vec2 pos,float width){
   return 1.0-smoothstep(0.01,0.02,abs(length(uv-pos)-1.0+width));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);

	float r = length( uv );
    //copied from iq
    // background color
	vec3 nightColor = vec3( 0.2, 0.2, 0.2 ) + 0.1*uv.y;
	vec3 dayColor   = vec3( 0.5, 0.6, 0.7 ) + 0.2*uv.y;

    float hors = mod( floor(iDate.w/3600.0), 24.0 );
	vec3 col = mix( nightColor, dayColor, smoothstep( 5.0, 7.0, hors ) - 
				                          smoothstep(19.0,21.0, hors ) );
    col = mix( col, vec3(0.0), 1.0-smoothstep(0.01,0.02,abs(r-0.9)) );

    float c0=1.0-smoothstep(0.01,0.02,length( uv)-0.9);
    c0=c0*step(uv.y*0.5,uv.y);
    col = mix( col, vec3(1.0), c0 );


    float c1=1.0-smoothstep(0.01,0.02,length( uv-vec2(0.455,0) )-0.445);
    c1=c1*step(uv.y*0.5,uv.y);
    col = mix( col, vec3(0.0), c1 );

    float c3=1.0-smoothstep(0.01,0.02,length( uv)-0.9);
    c3=c3*(1.0-step(uv.y*0.5,uv.y));
    col = mix( col, vec3(0.0), c3 );

    float c2=1.0-smoothstep(0.01,0.02,length( uv+vec2(0.455,0) )-0.445);
    c2=c2*(1.0-step(uv.y*0.5,uv.y));
    col = mix( col, vec3(1.0), c2 );

    float c4=1.0-smoothstep(0.01,0.02,length( uv+vec2(0.455,0) )-0.1);

    col = mix( col, vec3(0.0),c4 );

     float c5=1.0-smoothstep(0.01,0.02,length( uv-vec2(0.455,0) )-0.1);

    col = mix( col, vec3(1.0),c5 );

    // Output to screen
    fragColor = vec4(col,1.0);
}
