vec2 rotate(vec2 p, float ang)
{
    vec2 r = vec2(cos(ang),sin(ang));
    return p*mat2(r.x, -r.y, r.y, r.x);
}

float speed = 100.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ms = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord-.5 * iResolution.xy)/ms;

    float len = length(uv);
    float lig = asin(.001/len)*speed*iTime;
    vec2 ring = rotate(uv,lig+ iTime)/len;

    vec3 col = lig * vec3(sin(iTime),cos(iTime),0.)/vec3(ring, 1.)/5.;
    fragColor = vec4(col,1.0);
}
