void mainImage(out vec4 o, vec2 i) {
	float v,p=500.,s=p,q,w,u=i.x,b,f;
    for(;f!=2.&&f<3.;)
        q+u>p?
            v=(w+=p-s-q)/u*p,
            q=s,f+=.5
        :w>=0.?
            v=q+=u-w,
            s=w=0.,b++,f++:w;
    o=
      (s=abs(i.y*3.-p)*(w/u+b))<p?
      	texture(iChannel0,vec2(v,s)/p)
      :o; }
