void mainImage( out vec4 Q, in vec2 U )
{
    vec4 md = 0.25*(D(U+vec2(1,0))+D(U+vec2(0,1))+D(U-vec2(1,0))+D(U-vec2(0,1)));
    vec4 a = A(U);
    vec4 d = mix(D(U),md,0.02);
    vec4 m = C(U)-a;
    float l = length(a.xyz);
    if (l>0.) m.xyz -= .1*normalize(a.xyz);
    Q = d+(m);
    if (iFrame < 1) Q = vec4(0);
}
