const float PI = 3.14159;
void mainImage( out vec4 O, in vec2 uv ) {
    uv /= iResolution.y;   
    float t = iChannelTime[0] / 3.14159;
    uv.x -= t*0.3;
    uv.y += iResolution.y/(iResolution.x*t*5.);

    vec2 m = uv - 0.5;
    float a = atan(m.x, m.y) / PI;
    a = abs(a);
    float d = length(m) / 150.;

    // look at low freqs
    float v1 = texture(iChannel0, vec2(a * 0.6, .0)).r;
    v1 *= smoothstep(.8-t, .9, sin(d*20.-tan(iTime) - t*1.618033988*v1 + tan( t*1.618033988*v1)));
    v1 *= .5 + .5 * cos(d * 7.5 + PI) +  tan(d * 3.5 + PI);

    // look at high freqs
    float v2 = 5. * texture(iChannel0, vec2(d * 1.5 + 0.3, 0.01)).r;

    d = d + v2 + t * 3.;   
    vec3 c = vec3(v1) + 
        v2 * (0.5 + 0.5 * vec3(sin(d*8.0), sin(d*3.5), sin(d)));

    O = vec4(c, 2.0);
}
