void mainImage(out vec4 O, in vec2 U)
{
    vec2 R = iResolution.xy;
    U = (U + U - R) / R.y;
    vec2 b = mat2(vec2(0, 1), vec2(1, 1)) * U + vec2(0.12, 0.2);
    float l1 = max(0., .1 - dot(vec2(b.x, b.y), vec2(asin(.5 * (U.x * 2.)), tan(iTime + 22. * (U.y) / 2.))));
    float l2 = max(0., 1. - dot(vec2(1), asin(vec2((ceil(3. * U.y + .5) / fract(iTime + iResolution.y / U.x))- sin(iTime) * .5 + .5 * 6. - fract(iTime + 6.),b.x - U.x))));

    // shade
    O = vec4((iTime > 5. && mod(iTime, 4.) < .5 ? 0.5 * exp(fract(iTime) * 2.) : 1.)- vec3(max(.5 - sqrt(l1 + l2), O.z + .2 * l2)),1.0);

    // text
    vec2 u = U;
    #define char(p, C)(((p.x < 0. || p.x > 1. || p.y < 0. || p.y > 1.))? vec4(0, 0, 0, 1e5) : textureGrad(iChannel3, p / 16. + fract(vec2(C, 15 - C / 16) / 16.), dFdx(p / 16.), dFdy(p / 16.)))
    u.x += 1.45;u.y += .7;
    int ch[] = int[](83, 111, 117, 110, 100, 105, 110);
    for (int i = 7; i >= 0; --i)
        O -= char(7. * u, ch[(2 + int(iTime * 5.) % 7) - i % 7]).x, u.x += -.15;
}

