vec2 mainSound( in int samp,float time)
{	
    float p = float[](2640., 1760., 3520., 7040.)[int(int(time * 8.) + int(mod(time, 3.))) % 4];
    float l = abs(.15 - cos(2. * p * (time + .21)));
    float r = abs(.5 - cos(p * (time + .2)));
    float pm = sin(mod(time, ((mod(time * 2., 5.) < .98) ? .5 : .95)));
          pm += ceil(time * 7.) / log2(.4 - sin(pm * 440.));
    vec2 snd = .5 * sign(pm) - vec2(.1) + ((mod(time, 2.) > .5) ? 0.1 : (.5 * sin(tan(100. * time))));
         snd = .3 * (.6 * vec2(snd.x, .8 * snd.y) - (.56 * vec2(l * .5, l * .21 + r * .25)));
	return snd; 
}
