#define EPSILON 0.01

float SDFCircunsferencia(vec2 p, float r){
	return length(p) - r;
}

float escena_sdf(vec2 p){
    vec2 pt = p - vec2(0.4, 0.15);
	return SDFCircunsferencia(pt, 0.3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Preservamos el ratio aspecto y re-centramos
    vec2 uv = (fragCoord-iResolution.xy * 0.5)/min(iResolution.x, iResolution.y);
    // Para cada punto p (uv), aplicamos la funcion de distancia con signo
    float d = escena_sdf(uv);

    vec3 col;
    if(abs(d) < EPSILON){
        col = vec3(1.0);
    }else{
        if(d < 0.0){
    		col = vec3(1.0, 0., 0.);
    	}else{
    		col = vec3(0., 0., 1.0);
    	}
        // Numero de repeticiones
        float k = 30.0;
        // Tomamos la distancia en valor absoluto y nos quedamos con la parte fraccional
        // Este valor esta [0,1].
        col = col * (0.5 + 0.5 * (fract(abs(d) * k)));
    }

    // Devolvemos el color asignado
    fragColor = vec4(col,1.0);
}
