float PI = 3.1415926535;

vec2 rotate(vec2 v,float a){
    vec2 v2;
    v2.x = v.x * cos(a) - v.y *sin(a);
    v2.y = v.x * sin(a) + v.y * cos(a);
    return v2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 n = vec2(fragCoord - .5*iResolution.xy ) /iResolution.y;
    n = rotate(n,sin(pow(length(n),0.3)*10.0 + iTime) );
    float s = atan(n.y/ n.x);
    s = (s+PI) / (PI*2.0);

    //s = sign(cos(s*PI*2.0 * 6.0 + iTime));
    s = cos(s*PI*2.0 * 6.0 + iTime);
    s = clamp(s, 0.0,1.0);
    s = pow(log(s)/1.3,8.0) /256.0;
    s= clamp(s,0.0,1.0);

    vec4 c1 = vec4(0.0, 25.0, 53.0, 255.0)/255.0;
    vec4 c2 = vec4(252.0, 247.0, 232.0, 255.0) / 255.0;
    //vec4 c2 = vec4(1.);

    /*if(s >=1.0)
        fragColor = vec4(0.0, 25.0, 53.0, 255.0)/255.0;
    else
    	fragColor = vec4(1.0);*/
    fragColor = mix(c1,c2, s);
}
