void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 gbuffer=texture(iChannel0,fragCoord/iResolution.xy);

    bool renderBuffer=false;
    if(renderBuffer)
    {
        fragColor=gbuffer;
    }
    else
    {
        mat3 v = viewMat(iTime);

        vec2 ndc = fragCoord/iResolution.xy*2.-1.;
        vec3 view=normalize(v[2]+v[0]*ndc.x*iResolution.x/iResolution.y+v[1]*ndc.y);
        vec3 env = texture(iChannel2,view).rgb*2.;

        vec3 normal=vec3(gbuffer.rg, -sqrt(1.0-gbuffer.r*gbuffer.r+gbuffer.g*gbuffer.g));
        normal = normal*inverse(v);

        vec3 lightDir=normalize(vec3(0.707,0.707,-0.707));
        vec3 lightCol=texture(iChannel2,lightDir).rgb*70.;

        vec3 radiance=lightCol*max(dot(normal,lightDir),0.0);

        vec3 ambient=texture(iChannel3,normal).rgb*10.;

        // lambert
        vec3 albedo=vec3(gbuffer.b)/vec3(3.1415);

        vec3 illum=albedo*(radiance+ambient);
        illum=illum/(illum+vec3(1.));
        illum=pow(illum,vec3(1.0/2.2));

        vec3 o=mix(env,illum,float(length(gbuffer)!=0.));
        fragColor = vec4(o,1.);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 ndc = fragCoord/iResolution.xy*2.-1.;
    float d=0.;
    fragColor=vec4(0.);
    for(int i = 0; i < 16; i++)
    {
        mat3 v = viewMat(iTime);

        vec3 f = v[2];
        vec3 r = v[0];
        vec3 u = v[1];
        vec3 p = vec3(0.2*sin(iTime),0.,-0.2*cos(iTime))+normalize(f+r*ndc.x*iResolution.x/iResolution.y+u*ndc.y)*d;
        float s=length(p)-0.1;
        if(s<0.0001)
        {
            vec3 n = normalize(p);
            vec2 uv=vec2(acos(n.y)/3.1415,atan(n.z,n.x)/(3.1415*2.));

            n=n*v; // Transform our normals to the view's basis to make our z always point to -1

        	// Normalized lambert
        	vec3 albedo=texture(iChannel0,uv*2.).rgb;

            fragColor=vec4(n.xy,albedo.r,1.);
            break;
        }
        d+=s;
    }
}

mat3 viewMat(float time)
{
    vec3 f = vec3(-sin(time),0.,cos(time));
    vec3 r = vec3(cos(time),0.,sin(time));
    vec3 u = vec3(0.,1.,0.);

    return mat3(r,u,f);
}
