void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float d;
    float v;
    float r = 0.2;
    float col;

    uv *= 4.0;
    col = mod(uv.x, 2.0) * 1.0;
    col *= mod(uv.y, 2.0) * 1.0;
    uv = uv - floor(uv);
    uv -= 0.5;
    uv = sin(uv * iTime);
    uv.y *= iResolution.y/iResolution.x;
    d = length(uv);
    v = smoothstep(r, r-0.002, d);


    fragColor = vec4(vec3(v * col), 1.0);
}
