float Circle(vec2 uv, vec2 offset, float r, float blur)
{
  	uv.y /= 2.0;
    float d = distance(uv,offset);//+distance(uv,-offset);

    d = smoothstep( r, r - blur, d );

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;
	uv -= 0.5;
 	uv *= 2.0;


    float c = 0.0;

 //   c += Circle( uv, vec2(0., -0.5), 1., 0.0001 , scale, 1.0);

 //   c -= Circle( uv, vec2(0., -0.5-0.25), 1., 0.0001 , scale, 1.0);


     c += Circle( uv, vec2(0., 0.5), 1., 0.0001);

    // c -= Circle( uv, vec2(0., 0.5+0.25), 1., 0.0001 );

    fragColor = vec4(vec3(c) ,1.0);
}
