float bump(float x){
	float margin = 0.005;
	return smoothstep(-margin, 0., x) - smoothstep(0., margin, x);
}

float f(float x){
  return(x*sin(iTime*x)*x);  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.1;
    float ratio = iResolution.x/iResolution.y;
    // Normalized pixel coordinates
    vec2 uv = fragCoord/iResolution.y;
    uv.y -= 0.5;
    uv.x -= 0.5*ratio;
    uv/=zoom;
    // Axes
	//vec3 col = vec3( bump(abs(uv.x)) + bump(abs(uv.y)) );
    // Grid
    //vec2 guv = mod(uv,1.);
    //vec3 col = vec3( bump(abs(guv.x)) + bump(abs(guv.y)) );
    // Function
    vec3 col = vec3(bump(uv.y-f(uv.x))) * vec3(0.106, 0., 1.)*20.;

    // Output to screen
    fragColor = vec4(col,1.0);
}
