void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

     vec3 col;
 	for( int i=0; i<4; i++ )
	{ 
    float siz = pow( sin(float(i)*641.74+5.0)*.5 + 0.5, 4.0 );

      float rad = 0. + .5*siz;
    vec2 pos = vec2(.2*rad,.01*rad);
    vec2 bl = step(vec2(.4),uv)+rad;      
    vec2 tr = step(vec2(.4),1.0-uv)+rad;


    col = vec3(bl.x * bl.y * tr.x * tr.y);
    col.rg = uv;

}
    // Output to screen
    fragColor = vec4(col,1.0);
}
