void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 inputcoord = vec2(fragCoord.x*0.7 + fragCoord.y*0.3,
		fragCoord.y*0.7 - fragCoord.x*0.3 + iResolution.y / 4.) * 4.;
	inputcoord.x -= (pow(fract(-iTime / 4. + 0.2), 0.8) - 0.1)*6.*iResolution.x;
	inputcoord.y -= iResolution.y;
	vec2 pos = inputcoord / iResolution.y;
	vec2 uv = inputcoord / iResolution.xy;
	uv.y -= 0.5;
	pos -= 0.5;
	float t = iTime;
	float mask = 0.;
	if (uv.x > 0.0) {
		uv.y += sin(-t * 30. + uv.x * 15.)*0.3*uv.x;
		mask = 0.2 - abs(uv.y) - pow(uv.x / 3., 1.);
		if (mask > 0.) {
			mask = sin(mask * 3.14 / 2. / min(1., max(0., (0.2 - uv.x / 3.))));
		}
	}
	if (mask < 0.)
		mask = 0.;
	mask += pow(max(0., 1.45 - length(pos - vec2(-0.79, 0.))), 40.);
	if (mask > 0.5)
		mask = 1.;
	else
		mask = 0.;

	vec3 col;
	float ling = 0.9 - length(fragCoord / iResolution.x - 0.3);
	col.r = min(255.f, 0.745f + ling / 2.f);
	col.g = 0.745f + ling / 5.f;
	col.b = 1.f - ling * ling * 0.3f;
	float radius = length(pos - vec2(-0.79, 0.));
	if (radius<0.41 && radius>0.1)
		col = (col + (vec3(0.1, 0.4, 0.6) * mask) * 2.f) / 3.f;
	else
		if (mask > 0.)
			col = vec3(0.1, 0.4, 0.6) * mask / 2.;
	fragColor = vec4(col, 1.0);
}
