float lineDistance(vec2 a, vec2 b, vec2 p) {
    vec2 pa = p-a;
    vec2 ba = b-a;
	float t = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0);
    return length(pa-ba*t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = texture( iChannel0, uv ).xyz;

    /* bar for camera controls */
    const vec2 slider_pos = vec2(30.0, 30.0);
    const float slider_size = 200.0;
    const float slider_width = 8.0;
    if (lineDistance(slider_pos + vec2(0.0, -slider_width), slider_pos + vec2(slider_size, -slider_width),  fragCoord.xy) < 1.0 ||
        lineDistance(slider_pos + vec2(0.0, slider_width), slider_pos + vec2(slider_size, slider_width),  fragCoord.xy) < 1.0 ||
        lineDistance(slider_pos + vec2(0.0, -slider_width), slider_pos + vec2(0.0, slider_width),  fragCoord.xy) < 1.0 ||
        lineDistance(slider_pos + vec2(slider_size, -slider_width), slider_pos + vec2(slider_size, slider_width),  fragCoord.xy) < 1.0 
             ) {
        col = vec3(1.0, 1.0, 1.0);
    }

    fragColor = vec4( col, 1.0 );
}

// User parameters
const vec3 _SkyTint = vec3(0.8, 0.8, 0.8);
const float _AtmosphereThickness = 1.8; 

// constants
#define SUN_CONVERGENCE 100.0
#define OUTER_RADIUS 1.025 
#define kRAYLEIGH (mix(0.0, 0.0025, pow(_AtmosphereThickness,2.5))) 
#define kMIE 0.0010 
#define kSUN_BRIGHTNESS 30.0 
#define kMAX_SCATTER 50.0 
#define MIE_G (-0.990) 
#define MIE_G2 0.9801 
const vec3 ScatteringWavelength = vec3(.65, .57, .475);
const vec3 ScatteringWavelengthRange = vec3(.15, .15, .15);    
const float kOuterRadius = OUTER_RADIUS; 
const float kOuterRadius2 = OUTER_RADIUS*OUTER_RADIUS;
const float kInnerRadius = 1.0;
const float kInnerRadius2 = 1.0;
const float kCameraHeight = 0.0001;
const float kHDSundiskIntensityFactor = 15.0;
const float kSunScale = 400.0 * kSUN_BRIGHTNESS;
const float kKmESun = kMIE * kSUN_BRIGHTNESS;
const float kKm4PI = kMIE * 4.0 * 3.14159265;
const float kScale = 1.0 / (OUTER_RADIUS - 1.0);
const float kScaleDepth = 0.25;
const float kScaleOverScaleDepth = (1.0 / (OUTER_RADIUS - 1.0)) / 0.25;
const float kSamples = 2.0;

float Scale(float inCos)
{
	float x = 1.0 - inCos;
	return 0.25 * exp(-0.00287 + x*(0.459 + x*(3.83 + x*(-6.80 + x*5.25))));
}

vec4 ProceduralSkybox(vec3 ro, vec3 rd)
{
    vec3 _WorldSpaceLightPos0 = vec3(20, 20, 20);
    vec3 kSkyTintInGammaSpace = _SkyTint;
    vec3 kScatteringWavelength = mix(ScatteringWavelength-ScatteringWavelengthRange,ScatteringWavelength+ScatteringWavelengthRange,vec3(1,1,1) - kSkyTintInGammaSpace);
    vec3 kInvWavelength = 1.0 / (pow(kScatteringWavelength, vec3(4.0)));
    float kKrESun = kRAYLEIGH * kSUN_BRIGHTNESS;
    float kKr4PI = kRAYLEIGH * 4.0 * 3.14159265;
    vec3 cameraPos = vec3(0,kInnerRadius + kCameraHeight,0);
    vec3 eyeRay = rd;
    eyeRay.y = abs(eyeRay.y);
    float far = 0.0;
    vec3 cIn, cOut;

    far = sqrt(kOuterRadius2 + kInnerRadius2 * eyeRay.y * eyeRay.y - kInnerRadius2) - kInnerRadius * eyeRay.y;
    vec3 pos = cameraPos + far * eyeRay;
    float height = kInnerRadius + kCameraHeight;
    float depth = exp(kScaleOverScaleDepth * (-kCameraHeight));
    float startAngle = dot(eyeRay, cameraPos) / height;
    float startOffset = depth*Scale(startAngle);
    float sampleLength = far / kSamples;
    float scaledLength = sampleLength * kScale;
    vec3 sampleRay = eyeRay * sampleLength;
    vec3 samplePoint = cameraPos + sampleRay * 0.5;
    vec3 frontColor = vec3(0.0, 0.0, 0.0);
    for (int i=0; i<2; i++)
    {
        float height = length(samplePoint);
        float depth = exp(kScaleOverScaleDepth * (kInnerRadius - height));
        float lightAngle = dot(normalize(_WorldSpaceLightPos0.xyz), samplePoint) / height;
        float cameraAngle = dot(eyeRay, samplePoint) / height;
        float scatter = (startOffset + depth*(Scale(lightAngle) - Scale(cameraAngle)));
        vec3 attenuate = exp(-clamp(scatter, 0.0, kMAX_SCATTER) * (kInvWavelength * kKr4PI + kKm4PI));
        frontColor += attenuate * (depth * scaledLength);
        samplePoint += sampleRay;
    }
    cIn = frontColor * (kInvWavelength * kKrESun);
    cOut = frontColor * kKmESun;

    vec3 skyColor = (cIn * (0.75 + 0.75 * dot(normalize(_WorldSpaceLightPos0.xyz), -eyeRay) * dot(normalize(_WorldSpaceLightPos0.xyz), -eyeRay))); 
    vec3 ray = -rd;
    vec3 color = skyColor;
    return vec4(sqrt(color),1.0);      
}

// Reference paper https://graphics.pixar.com/library/ProductionVolumeRendering/paper.pdf

////////////////////////////////////////////////////////////////////////////////////

#define PI 3.141592654f
#define TAU ( PI * 2.0f )
#define EPSILON .0001

#define MAX_VOLUME_DEPTH 1000
#define MAX_RADIO_VOLUME_DEPTH 1000000

// Volume definition
int volume_type = 0; // Can be 0, 1, or 2
float linear_attenuation_unit = .03; // Should be less than the "minimum" feature size
float majorant_extinction = 1.0f;
float max_grid_value = 1.0f;

// Absorption and Scattering here must sum to less than or equal to the majorant
#define ABSORPTION 0.1f
#define SCATTERING 0.9f

#define EMISSION 0.f

vec3 bbmin = vec3(-1.0);
vec3 bbmax = vec3(1.0);

uint seed;	//seed initialized in main

uint wang_hash()
{
    seed = uint(seed ^ uint(61)) ^ uint(seed >> uint(16));
    seed *= uint(9);
    seed = seed ^ (seed >> 4);
    seed *= uint(0x27d4eb2d);
    seed = seed ^ (seed >> 15);
    return seed;
}

float rnd()
{
    return float(wang_hash()) / 4294967296.0;
}

//pos in uv space where we store various data
const vec2 acc_start_uv = vec2(0.0); //accumulation start frame
const vec2 camera_pos_uv = vec2(1.0, 0.0); //camera pos
const vec2 camera_dir_uv = vec2(2.0, 0.0); //camera pos

int getAccStart() {
	if( iMouse.z > 0.0 ) {
        return iFrame;
    } else {
        return int(texture( iChannel0, (acc_start_uv + vec2(0.5, 0.5))/iResolution.xy ).x);
    }
}

vec4 LoadVec4(vec2 uv) { return texture( iChannel0, (uv + vec2(0.5, 0.5))/iResolution.xy ); }


struct Ray {
    vec3 origin;
    vec3 dir;
};

struct Camera {
    mat3 rotate;
    vec3 pos;
    float fovV;
    float lensSize;
    float focusDist;
    vec2 iPlaneSize;
};

// ************ CAMERA STUFF ***************
Camera camera;
Camera cameraOld;

void initCamera( 	in vec3 pos,
                	in vec3 target,
                	in float fovV,
                	in float radius,
                	in float focus_dist,
                	out Camera c
               ) {
    const vec3 upDir = vec3( 0.0, 1.0, 0.0 );
    c.rotate[2] = normalize( pos-target ); //back
	c.rotate[0] = normalize( cross( upDir, c.rotate[2] ) ); //right
	c.rotate[1] = cross( c.rotate[2], c.rotate[0] ); //up
    c.fovV = fovV;
    c.pos = pos;
    c.focusDist = focus_dist;
    c.lensSize = radius;
    c.iPlaneSize = 2.*tan(0.5*c.fovV)*vec2(iResolution.x/iResolution.y,1.);
}

Ray genRay( in Camera c, in vec2 uv, in vec2 xi ) {
    Ray ray;

    vec2 ixy = (uv-0.5)*c.iPlaneSize;
	vec3 dirLocal = normalize(vec3(ixy, -1.0));
    vec3 posGlobal = c.pos;
	vec3 dirGlobal = c.rotate*dirLocal;
	return Ray(posGlobal, dirGlobal);
}

mat4 LookAt(vec3 eye, vec3 at, vec3 up)
{
  vec3 zaxis = normalize(eye - at);    
  vec3 xaxis = normalize(cross(zaxis, up));
  vec3 yaxis = cross(xaxis, zaxis);

  zaxis = -zaxis;

  mat4 viewMatrix = mat4(
    vec4(xaxis.x, xaxis.y, xaxis.z, -dot(xaxis, eye)),
    vec4(yaxis.x, yaxis.y, yaxis.z, -dot(yaxis, eye)),
    vec4(zaxis.x, zaxis.y, zaxis.z, -dot(zaxis, eye)),
    vec4(eye, 1)
  );

  return viewMatrix;
}


