vec3 miss_color(in vec3 ray_dir) {
    return vec3(ProceduralSkybox(vec3(0.0), ray_dir));
    //return texture(iChannel1,ray_dir).rgb;

}

////////////////////////////////////////////////////////////////////////////////////

bool intersect_volume_box(in vec3 raypos, in vec3 raydir, out float tmin, out float tmax) {
    float x0 = (-0.5f - raypos.x) / raydir.x;
    float y0 = (-0.5f - raypos.y) / raydir.y;
    float z0 = (-0.5f - raypos.z) / raydir.z;
    float x1 = ( 0.5f - raypos.x) / raydir.x;
    float y1 = ( 0.5f - raypos.y) / raydir.y;
    float z1 = ( 0.5f - raypos.z) / raydir.z;
    tmin = max(max(max(min(z0,z1), min(y0,y1)), min(x0,x1)), 0.0f);
    tmax = min( min( max(z0, z1), max(y0, y1 ) ), max(x0, x1) );
    return (tmin < tmax);
}

bool in_volume(in vec3 pos) {
    return max(abs(pos.x),max(abs(pos.y),abs(pos.z))) < 0.5f;
}

float sample_volume(in vec3 p) {
    if (volume_type == 0) {
        vec3 pos = p + vec3(0.5f, 0.5f, 0.5f);
        int steps = 3;
        for (int i = 0; i < steps; ++i) {
            pos *= 3.0f;
            int s = (int(pos.x) & 1) + (int(pos.y) & 1) + (int(pos.z) & 1);
            if (s >= 2)
                return 0.0f;
        }
        return max_grid_value;
    } else if (volume_type == 1) {
        float r = 0.5f * (0.5f - abs(p.y));
        float a = float(PI * 8.0) * p.y;
        float dx = (cos(a) * r - p.x) * 2.0f;
        float dy = (sin(a) * r - p.z) * 2.0f;
        return pow(max((1.0f - dx * dx - dy * dy), 0.0f), 5.0f) * max_grid_value;
    }
    else {
        return max_grid_value;
    }
}

float sample_volume_absorption(in vec3 p) {
    // a bunch of different ways to do this...
    return sample_volume(p) * ABSORPTION;
}

float sample_volume_scattering(in vec3 p) {
    // a bunch of different ways to do this...
    return sample_volume(p) * SCATTERING;
}

float sample_volume_emission(in vec3 p) {
	// a bunch of different ways to do this...
    return sample_volume(p) * EMISSION;
}

/// Taken and modified from Algorithm 3 in "Pixar's Production Volume Rendering" paper.
/// \param x The origin of the ray, somewhere inside the volume.
/// \param w The direction of the light (opposite of ray direction). 
/// \param d The distance along the ray to the boundary.
/// \returns Transmittance from the ray origin to the boundary
float RatioTracking(vec3 x, vec3 w, float d) {
    vec3 x0 = x;
    float T = 1.0f; // Set Monte-Carlo weight to 1
    for (int i = 0; i < MAX_RADIO_VOLUME_DEPTH; ++i) {
        float rand = rnd();
        float t = (-log(1.0f - rand) / majorant_extinction) * linear_attenuation_unit;

        // A boundary has been hit
        if (t >= d) {
            return T;
        }

        // Update current position
    	x = x0 - t * w;

    	float absorption = sample_volume_absorption(x);
        absorption = absorption / majorant_extinction;

        // Update Monte Carlo weight
        T = T * (1.f - absorption);

        // Biases, but terminates faster. 
        if (T < EPSILON) break;
    }

    return 1.f;
}

/// Taken and modified from Algorithm 1 in "Spectral and Decomposition 
/// Tracking for Rendering Heterogeneous Volumes" paper.
/// Allows the majorant_extinction to be approximate without biasing the volume
/// \param x The origin of the ray.
/// \param w The direction of the light (opposite of ray direction). 
/// \param d The distance along the ray to the boundary.
/// \param t The returned hit distance.
/// \param weight Reweights the returned sample to remain unbiased with underestimating majorants
/// \param event Will be updated to represent the event that occured during tracking.
///              0 means the boundary was hit
///              1 means an absorption/emission occurred
///              2 means a scattering collision occurred
///              3 means a null collision occurred
void SampleWeightedDeltaTracking(vec3 x, vec3 w, float d, out float t, out float weight, out int event) {
    float rand1 = rnd();
    float rand2 = rnd();

    // Set new t for the current x.
    t = (-log(1.0f - rand1) / majorant_extinction) * linear_attenuation_unit;

    // A boundary has been hit
    if (t >= d) {
    	event = 0;
        t = d;
        weight = 1.f;
        return;
    }

    // Update current position
    x = x - t * w;

   	float absorption = sample_volume_absorption(x);
    float scattering = sample_volume_scattering(x);
    float extinction = absorption + scattering;
    //float null_collision = 1.f - extinction;
    float null_collision = majorant_extinction - extinction;


    float pa = absorption / (extinction + abs(null_collision));
    float ps = scattering / (extinction + abs(null_collision));
    float pn = abs(null_collision) / (extinction + abs(null_collision));


    //extinction = extinction / majorant_extinction;
    //absorption = absorption / majorant_extinction;
    //scattering = scattering / majorant_extinction;
    //null_collision = null_collision / majorant_extinction;

    // An absorption/emission collision occured
    if (rand2 < pa) 
    {
        weight = absorption / (majorant_extinction * pa);
    	event = 1;
        return;
    }

    // A scattering collision occurred
    //else if (rand2 < 1.f - pn) {
    else if (rand2 < (ps + pa)) {
        weight = scattering / (majorant_extinction * ps);
        event = 2;
        return;
    }

    // A null collision occurred
    else {
        weight = null_collision / (majorant_extinction * pn);
        event = 3;
        return;    	
    }
}

/// Taken and modified from Algorithm 1 in "Spectral and Decomposition 
/// Tracking for Rendering Heterogeneous Volumes" paper.
/// Implements the top level delta tracking algorithm, returning radiance.
/// \param seed The seed to use by the random number generator.
/// \param x The origin of the ray.
/// \param w The direction of the light (opposite of ray direction). 
/// \param d The distance along the ray to the boundary.
vec3 WeightedDeltaTracking(uint seed, vec3 x, vec3 w) {
    float t0, t1;
    if (!intersect_volume_box(x, -w, t0, t1)) return miss_color(-w);

    // Move ray to volume boundary
    x = x - t0 * w;
    t1 = t1 - t0;
    t0 = 0.f;

    // Note: original algorithm had unlimited bounces. 
    vec3 throughput = vec3(1.f);
    for (int i = 0; i < MAX_VOLUME_DEPTH; ++i) {
        int event = 0;
        float t = 0.f;
        float weight;
        SampleWeightedDeltaTracking(x, w, t1, t, weight, event);
        //return vec3(weight);
        throughput = throughput * weight;
        x = x - t * w;

        // A boundary has been hit. Sample the background.
        if (event == 0) return miss_color(-w) * throughput;

        // An absorption / emission occurred.
        if (event == 1) return vec3(sample_volume_emission(x)) * throughput;

        // A scattering collision occurred.
        if (event == 2) {            
            // Sample isotropic phase function to get new ray direction           
            float phi = TAU * rnd();
            float cos_theta = 1.0f - 2.0f * rnd();
            float sin_theta = sqrt (1.0f - cos_theta * cos_theta);
            w = -vec3(cos(phi) * sin_theta, sin(phi) * sin_theta, cos_theta);

            // Compute updated boundary
            if (!intersect_volume_box(x, -w, t0, t1)) return miss_color(-w) * throughput;
        }

        // A null collision occurred.
        if (event == 3) {
            // update boundary in relation to the new collision x, w does not change.
            t1 = t1 - t;
        }
    }

    // If we got stuck in the volume
    return vec3(1.0, 0.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    seed = uint(uint(fragCoord.x) * uint(1973) + uint(fragCoord.y) * uint(9277) + uint(iFrame) * uint(26699)) | uint(1);    

    // Camera Slider
    float fov = radians(30.0);
    vec3 camera_pos = (iFrame == 0)? 1.5f*vec3( 1.0, 1.0,  1.0 ) : LoadVec4(camera_pos_uv).xyz;
    vec3 camera_dir = (iFrame == 0)? vec3(0.0) : LoadVec4(camera_dir_uv).xyz;
    initCamera(camera_pos, camera_dir, fov, 0.0, 9.2, cameraOld);
    if(iMouse.z > 0.0) {
        const vec2 slider_pos = vec2(30.0, 30.0);
        const float slider_size = 200.0;
    	const float slider_width = 8.0;

        if (iMouse.x > slider_pos.x &&
            iMouse.x < slider_pos.x + slider_size &&
            iMouse.y > slider_pos.y - slider_width &&
            iMouse.y < slider_pos.y + slider_width ) {
        	float slider = (iMouse.x - slider_pos.x) / slider_size;

            slider -= 0.5; //(-0.5, 0.5)

            camera_pos += vec3( 1.0, 0.0,  0.0 ) * slider * 0.3;
        }
    }

    initCamera(camera_pos, camera_dir, fov, 0.0, 9.2, camera);


    vec2 uv = (fragCoord.xy + vec2( ( rnd() ), rnd() )) / iResolution.xy;
    Ray ray = genRay( camera, uv, vec2(rnd(), rnd()) );

    // With weighted delta tracking, the majorant doesn't need to be
    // larger than all voxel values.
    // However, highly non-bounding majorants cause a rapid increase 
    // in variance.
    if (fragCoord.x < iResolution.x * .3) majorant_extinction *= .97;
    if (fragCoord.x < iResolution.x * .4) majorant_extinction *= .98;
    if (fragCoord.x < iResolution.x * .5) majorant_extinction *= .99;

    vec3 Li = vec3(WeightedDeltaTracking(seed, ray.origin, -ray.dir));

    vec3 accumulatedColor = Li;


	// Save current camera state 
    vec3 col_acc;
    vec2 coord = floor(fragCoord.xy);
    if(all(equal(coord.xy, acc_start_uv))) col_acc = vec3(getAccStart());
	else if(all(equal(coord.xy, camera_pos_uv))) col_acc = camera_pos;
    else if(all(equal(coord.xy, camera_dir_uv))) col_acc = camera_dir;
    else 
    {
        // Accumulate.
        vec2 uv = fragCoord / iResolution.xy;
        vec3 prev = texture(iChannel0,uv).rgb;
        vec3 col = prev + (accumulatedColor - prev) / (float(iFrame) + 1.0f);

        if(iFrame == 0) {
            col_acc = accumulatedColor;
        } else {            
            int frame_start = getAccStart();//int(texture( iChannel3, vec2(0.5, 0.5) / iResolution.xy ).x);
            int spp1 = iMouse.z > 0.0 ? 0 : iFrame - frame_start;
            int spp2 = 1;
            vec3 col_new = accumulatedColor;
            col_acc = texture( iChannel0, fragCoord/iResolution.xy ).xyz;
            col_acc = mix(col_acc, col_new, float(spp2)/float(spp1+spp2));
        }
    }

    fragColor = vec4( col_acc, 1.0 );
}
