float RayMarch(vec3 ro, vec3 rd)
{
	float dO=0.2;
    //Determines size of shadow

    for(int i=0; i<MAX_STEPS; i++) 
    {
    	vec3 p = ro + rd*dO;

        float dS = GetDist(p);

        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) 
        {
            gres2 = vec2(dO,dS); 
            break;
    	}    

    }

    return dO;
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec2 res = gres2; //RayMarch(ro,rd);
    vec3 Ambient= vec3(0.1,0.1,0.1);

    float t = res.x;
	float m = res.y;

    if (mObj.tipo==12.0 )
     { return  getSphereColor(int( mObj.tipo)) ; }

    vec3 pos = ro + t * rd;
    vec3 normal = vec3(0,0,0);
    vec3 col = vec3(0,0,0); 

    if(m <= -1.0)//skyBox
    {
        col = Ambient;
    } 
    else     
    {
        if(m <= 1.0)  {
        	float m = marble(pos);
        	m = 0.5*m;
        	col = marbleColor(m);
        }
    	else     
    	{

            float m = marble(pos);
        	m = 0.5*m+0.2;
        	col =  marbleColor(m);
    	}	
    }
    return col;
}



vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    //Texture of white and black in image
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}



float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(sin(iTime), cos(iTime)*2.);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


float GetLightv2(vec3 p) {
    vec3 lightPos = vec3(0, 5, 6);
    //Determine movement of light ex. shadow and light direction and diffusion
    lightPos.xz += vec2(1, 2);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}


