Main
{
    Neighborhood
    vec3 no = normalize(vec3(grad,.25));
    vec3 re = reflect(no,vec3(0,0,1));
    re.yz *= rot(.3*iTime);
    re.zx *= rot(.25*iTime);
    Q = (0.5+0.7*A(U).xxxx)*(0.5+0.7*re.xzyz);
}

Main
{
    U-=0.5*R;
    float a = 0.02*sin(.3*iTime)*exp(-3.*length(U)/R.y);
